import axios from "axios"
import {settings} from "../config"

export const allblogs=async ()=>
{
    const url=settings.server+'/bloggers/bloglist'
    const token=sessionStorage['token']
    let result
    try
    {
        result=await axios.get(url,{headers:{
            Authorization:`Bearer ${token}`,
        }},)
        result=result.data
    }
    catch(ex)
    {
        result=ex
    }
    return result
}
export const createblog=async (blogTitle,blogContent,blogTags)=>
{
    const url=settings.server+'/bloggers/createblog'
    const blogDate=Date.now()
    console.log(blogDate)
    const token=sessionStorage['token']
    let result
    try
    {
        result=await axios.post(url,{
            blogTitle,blogContent,blogTags,blogDate
        },{headers:{
            Authorization:`Bearer ${token}`,
        }},)
        result=result.data
    }
    catch(ex)
    {
        result=ex
    }
    return result
}
export default async function deleteBlog(blogTitle)
{
    const url=settings.server+'/bloggers/deleteblog'
    const token=sessionStorage['token']
    let result
    try
    {
        result=await axios.delete(url,{headers:{
            Authorization:`Bearer ${token}`,
        }},{
            blogTitle
        },)
        result=result.data
    }
    catch(ex)
    {
        result=ex
    }
    return result   
}
