import {Link,useNavigate} from 'react-router-dom'
import { Button } from 'react-bootstrap'
import { useEffect, useState } from 'react'
import deleteBlog from '../services/blog.services'
import getauthor from '../services/user.services'
export default function Blog(props)
{
    const{id,blogTitle,blogContent,blogDate,blogTags,userId,comments,user,getuser}=props
    let[authors,getauthor]=useState('')
    const nav=useNavigate()
    useEffect(()=>{
        loadblogs(userId,getauthor)
    })
    async function delblog(title)
    {
        console.log(title)
        const result=await deleteBlog(title)
    }

    function ublog()
    {
        nav('/updateblog')
    }

    const loadblogs=async (userId,func)=>{
        authors=await getuser(userId)
        func(authors)
    }

    function ob()
    {
        console.log(blogTitle)
        //nav('openblog')
    }

    return(
        <div>
            <div className="card" style={{border:"1px solid black",margin:"5px",padding:"5px",background:"#cbaaee",color:'black'}}>
                        <div className="card-body">
                            <h3 className="card-title" style={{textAlign:'center'}}>{blogTitle}</h3>
                            <h6>By {authors}</h6>
                            <textarea className="card-text" rows={2} contentEditable="false">{blogContent}</textarea>
                            
                            <Link to={ob} style={{textDecoration:'none'}}>Read More</Link>
                            
                        </div>
            </div>
        </div>
    )

    /*<div style={{float:'right'}}>
                                <Button variant="primary" style={{margin:'5px'}} onClick={ublog}>Update</Button>
                                <Button variant="danger" style={{margin:'5px'}} onClick={delblog({blogTitle})}>Delete</Button>
                            </div>*/
}
