import {Link,useNavigate} from 'react-router-dom'
import { Button } from 'react-bootstrap'
import deleteBlog from '../services/blog.services'
import getauthor from '../services/user.services'
export default function Blog(props)
{
    const{id,blogTitle,blogContent,blogDate,blogTags,userId,comments,user}=props
    async function delblog(title)
    {
        const result=await deleteBlog(title)
        //alert(result)
    }
    async function getuser(uid)
    {
        console.log("inside getuser componenet")
        console.log(uid)
        console.log(await getauthor(uid))
    }
    return(
        <div>
            <div class="card" style={{border:"1px solid black",margin:"5px",padding:"5px",background:"#cbaaee",color:'black'}}>
                        <div class="card-body">
                            <h3 class="card-title" style={{textAlign:'center'}}>{blogTitle}</h3>
                            <h4>{userId}</h4>
                            {getuser(userId)}
                            <p class="card-text" aria-rowcount={2}>{blogContent}</p>
                            <Link to={'#'} style={{textDecoration:'none'}}>Read More</Link>
                        </div>
            </div>
        </div>
    )

    /*<div style={{float:'right'}}>
                                <Button variant="primary" style={{margin:'5px'}}>Update</Button>
                                <Button variant="danger" style={{margin:'5px'}} onClick={delblog({blogTitle})}>Delete</Button>
                            </div>*/
}
