import {Link,useNavigate} from 'react-router-dom'
import { useEffect, useState } from 'react'
import { allblogs } from '../services/blog.services'
import Blog from "../components/blog_component"
export default function Main_blog(props)
{
    const[rating,setRating]=useState('')
    const[blogs,setBlogs]=useState('')

    useEffect(()=>{
        loadblogs()
    })

    const loadblogs=async ()=>{
        const result=await allblogs()
        if(result)
        {
            setBlogs(result)
        }
    }

    return (
        <div>
            <h1 className="header" style={{fontSize:'22px'}}>Get Blogs</h1>
            <Link to="/createblog" class="btn btn-outline-info">Create Blog</Link>
            <div className='row'>
                <div>
                    {blogs.length>0 && blogs.map(blog=>{
                        const[id,blogTitle,blogContent,blogDate,blogTags,userId,comments,user]=blog
                        return <Blog
                            key={id}
                            id={id}
                            blogTitle={blogTitle}
                            blogContent={blogContent}
                            blogDate={blogDate}
                            blogTags={blogTags}
                            userID={userId}
                            comments={comments}
                            user={user}
                        />
                    })}
                    
                </div>
            </div>
        </div>
    )
}
