import {Link,useNavigate} from 'react-router-dom'
import { useEffect, useState } from 'react'
import deleteBlog, { allblogs, getblog } from '../services/blog.services'
import Dropdown from 'react-bootstrap/Dropdown'
import Blog from "../components/blog_component"
import getauthor from '../services/user.services'
export default function Main_blog(props)
{
    const[rating,setRating]=useState('')
    const[blogs,setBlogs]=useState('')
    const nav=useNavigate()

    useEffect(()=>{
        loadblogs()
    },[])

    const loadblogs=async ()=>{
        const result=await allblogs()
        if(result)
        {
            setBlogs(result)
        }
    }
    async function getuser(userId)
    {
        const result=await getauthor(userId)
        if(result)
        {
            return await getauthor(userId)
        }
    }

    async function openb(id)
    {
        console.log(id)
        const result=await getblog(id)
        return result
    }

    async function dblog(id)
    {
        console.log(id)
        const result=await deleteBlog(id)
        return result
    }

    const logout=()=>{
        sessionStorage.removeItem('token')
        nav('/signin')
    }
    const dispuser=sessionStorage['useremail']

    return (
        <div>
            <h1 className="header" style={{fontSize:'22px'}}>Get Blogs</h1>
            <Dropdown className="d-inline mx-2" autoClose="outside">
                <Dropdown.Toggle id="dropdown-autoclose-outside">
                Welcome {dispuser}
                </Dropdown.Toggle>
                <Dropdown.Menu>
                <Dropdown.Item ><Link to="/createblog" className="btn btn-outline-info">Create Blog</Link></Dropdown.Item>
                <Dropdown.Item ><Link to="/updateuser" className="btn btn-outline-info">Update Profile</Link></Dropdown.Item>
                <Dropdown.Item ><button className="btn btn-outline-warning" onClick={logout}>Logout</button></Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
            <div><input className="btn btn-outline-info" type="text" /><button className="btn btn-outline-info"> Search </button></div>
            <div className='row'>
                <div>
                    {blogs.length>0 && blogs.map(blog=>{
                        const{id,blogTitle,blogContent,blogDate,blogTags,userId,comments,user}=blog
                        //console.log(userId)
                        return <Blog
                            key={id}
                            id={id}
                            blogTitle={blogTitle}
                            blogContent={blogContent}
                            blogDate={blogDate}
                            blogTags={blogTags}
                            userId={userId}
                            comments={comments}
                            user={user}
                            getuser={getuser}
                            openb={openb}
                            dblog={dblog}
                        />
                    })}
                    
                </div>
            </div>
        </div>
    )
}
