import {Link,useNavigate} from 'react-router-dom'
import { Button } from 'react-bootstrap'
import { useEffect, useState } from 'react'
import deleteBlog from '../services/blog.services'
import getauthor from '../services/user.services'
export default function Blog(props)
{
    const{id,blogTitle,blogContent,blogDate,blogTags,userId,comments,user,getuser,openb,dblog}=props
    let[authors,getauthor]=useState('')
    const nav=useNavigate()
    useEffect(()=>{
        loadblogs(userId,getauthor)
        sessionStorage["authors"]=authors
    })
    const delblog=async()=>
    {
        const uid=id
        const result=await dblog(uid)
    }

    function ublog()
    {
        nav('/updateblog')
    }

    const loadblogs=async (userId,func)=>{
        authors=await getuser(userId)
        func(authors)
    }

    async function ob()
    {
        console.log(id)
        const result=await openb(id)
        console.log(result)
        sessionStorage["title"]=result.blogTitle
        sessionStorage["content"]=result.blogContent
        sessionStorage["date"]=result.blogDate
        sessionStorage["tag"]=result.blogTags
        nav('/openblog')
    }

    return(
        <div>
            <div className="card" style={{border:"1px solid black",margin:"5px",padding:"5px",background:"#cbaaee",color:'black'}}>
                        <div className="card-body">
                            <h3 className="card-title" style={{textAlign:'center'}}>{blogTitle}</h3>
                            <h6>By {authors}</h6>
                            <p rows={1} className="card-text">{blogContent}</p>
                            <button onClick={ob}>Read More</button>
                            <button onClick={delblog}>Delete</button>
                        </div>
            </div>
        </div>
    )

    /*<div style={{float:'right'}}>
                                <Button variant="primary" style={{margin:'5px'}} onClick={ublog}>Update</Button>
                                <Button variant="danger" style={{margin:'5px'}} onClick={delblog({blogTitle})}>Delete</Button>
                            </div>*/
}
