import {Link,useNavigate} from 'react-router-dom'
import { useState } from 'react'
export default function Open_blog(props){
    const[comment,setComment]=useState('')
    const authors=sessionStorage["authors"]

    return(
        <div>
            <h1 className="header" style={{fontSize:'22px'}}>Opened Blog</h1>
            <div class="card" style={{background:'teal'}}>
                <div class="card-body">
                    <h3 class="card-title">{sessionStorage.getItem('title')}</h3>
                    <h6 class="card-title">Created By {authors} on {sessionStorage.getItem('date').slice(0,10)}</h6>
                    <p class="card-text" style={{fontSize:'24px',margin:'5px'}}>{sessionStorage.getItem('content')}</p>
                    <p class="card-text" style={{fontSize:'18px',border:'1px solid black',borderRadius:'5px'}}>Tag:{sessionStorage.getItem('tag')}</p>
                    <h4>Comments</h4>
                    <h6>Add Comment</h6>
                    <textarea className="form-control" aria-label="Comments" onChange={
                            (e)=>{
                                setComment(e.target.value)
                            }}></textarea>
                    <button>Post Comment</button>
                </div>
            </div>
        </div>
    )
}
