import {Link,useNavigate} from 'react-router-dom'
import { useEffect, useState } from 'react'
import { allblogs } from '../services/blog.services'
import Dropdown from 'react-bootstrap/Dropdown'
import Blog from "../components/blog_component"
export default function Main_blog(props)
{
    const[rating,setRating]=useState('')
    const[blogs,setBlogs]=useState('')

    useEffect(()=>{
        loadblogs()
    })

    const loadblogs=async ()=>{
        const result=await allblogs()
        if(result)
        {
            setBlogs(result)
        }
    }

    return (
        <div>
            <h1 className="header" style={{fontSize:'22px'}}>Get Blogs</h1>
            <Dropdown className="d-inline mx-2" autoClose="outside">
                <Dropdown.Toggle id="dropdown-autoclose-outside">
                Welcome
                </Dropdown.Toggle>
                <Dropdown.Menu>
                <Dropdown.Item href="#"><Link to="/createblog">Create Blog</Link></Dropdown.Item>
                <Dropdown.Item href="#"><Link to="/updateprofile" className="btn btn-outline-info">Update Profile</Link></Dropdown.Item>
                <Dropdown.Item href="#">Menu Item</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
            <div className='row'>
                <div>
                    {blogs.length>0 && blogs.map(blog=>{
                        const{id,blogTitle,blogContent,blogDate,blogTags,userId,comments,user}=blog
                        return <Blog
                            key={id}
                            id={id}
                            blogTitle={blogTitle}
                            blogContent={blogContent}
                            blogDate={blogDate}
                            blogTags={blogTags}
                            userID={userId}
                            comments={comments}
                            user={user}
                        />
                    })}
                    
                </div>
            </div>
        </div>
    )
}
