import axios from "axios"
import {settings} from "../config"

export const allblogs=async ()=>
{
    const url=settings.server+'/bloggers/bloglist'
    let result
    try
    {
        result=await axios.get(url,{
        },{headers:{
            Authorization:`Bearer ${token}`,
        }},)
        result=result.data
    }
    catch(ex)
    {
        result=ex
    }
    return result
}
export const createblog=async (blogTitle,blogContent,blogTags)=>
{
    const url=settings.server+'/bloggers/createblog'
    const blogDate=Date.now()
    console.log(blogDate)
    const token=sessionStorage['token']
    let result
    try
    {
        result=await axios.post(url,{
            blogTitle,blogContent,blogTags,blogDate
        },{headers:{
            Authorization:`Bearer ${token}`,
        }},)
        result=result.data
    }
    catch(ex)
    {
        result=ex
    }
    return result
}
export const update=async (firstName, lastName, userEmail, userPassword, userCity, userState, userCountry, userPostalCode, userBirthDate, userGender)=>
{
    const url=settings.server+'/bloggers/updateprofile'
    let result
    try
    {
        result=await axios.put(url,{
            firstName, lastName, userEmail, userPassword, userCity, userState, userCountry, userPostalCode, userBirthDate, userGender
        })
        result=result.data
    }
    catch(ex)
    {
        result=ex
    }
    return result
}
