import {Link,useNavigate} from 'react-router-dom'
import { useState } from 'react'
export default function Open_blog(props){
    const[comment,setComment]=useState('')
    const[id,blogTitle,blogcontent,date,comments,ratings]=sessionStorage["openingblog"]
    const authors=sessionStorage["authors"]

    return(
        <div>
            <h1 className="header" style={{fontSize:'22px'}}>Opened Blog</h1>
            <div class="card" >
                <div class="card-body">
                    <h5 class="card-title">{blogTitle}</h5>
                    <h6 class="card-title">Created By {authors} on {date}</h6>
                    <p class="card-text" style={{fontSize:'24px'}}>{blogcontent}</p>
                    <p class="card-text" style={{fontSize:'18px'}}>{comments}</p>
                    <h4>Comments</h4>
                    <h6>Add Comment</h6>
                    <textarea className="form-control" aria-label="Comments" onChange={
                            (e)=>{
                                setComment(e.target.value)
                            }}></textarea>
                </div>
            </div>
        </div>
    )
}
